$(document).ready(function(){$("#newsletterListSubscribe").on("submit",function(e){e.preventDefault();let mailAddress=$(this).find('[name="mailAddress"]').val();$(this).find("submit").attr("disabled","disabled").addClass("disabled");$.ajax({url:"/service/NewsletterList/Create",type:"post",dataType:"json",contentType:"application/json",data:JSON.stringify({mailAddress:mailAddress}),success:function(){Swal.fire({title:"Başarıyla tamamlandı!",html:"Bültene başarıyla <strong>"+mailAddress+"</strong> e-posta adresiyle kayıt oldunuz!",icon:"success",confirmButtonText:"Tamam"})},error:function(response){let jsonParse=JSON.parse(response.responseText);let errorMessage=jsonParse[0]["errorMessage"];Swal.fire({title:"Bir hata meydana geldi!",text:errorMessage,icon:"error",confirmButtonText:"Tamam"})},done:function(){$(this).find("submit").removeAttr("disabled").removeClass("disabled")}})})});